    FROM
        `mydb`.`conta_a_pagar_has_pagamento` `cr`
    WHERE
        (`cr`.`desativado` = FALSE)
    GROUP BY `cr`.`dataPagamento` , `cr`.`status`
    HAVING ((`cr`.`dataPagamento` >= CURDATE())
        AND ((`cr`.`status` = 'PENDENTE')
        OR (`cr`.`status` = 'CONFERIDO')));

