        LEFT JOIN `mydb`.`estoque_has_compra` `ehc` ON ((`e`.`idEstoque` = `ehc`.`idEstoque`)))
        LEFT JOIN `mydb`.`pedido_fornecedor_has_produto` `pf` ON (((`ehc`.`idCompra` = `pf`.`idCompra`)
