        LEFT JOIN `mydb`.`produto` `p` ON ((`e`.`idProduto` = `p`.`idProduto`)))
    WHERE
        ((`e`.`status` = 'EM COLETA')
            AND ISNULL(`pf`.`dataPrevColeta`)
