USE `mydb`;
CREATE 
     OR REPLACE ALGORITHM = UNDEFINED 
    DEFINER = `root`@`localhost` 
    SQL SECURITY DEFINER
VIEW `mydb`.`view_financeiro` AS
    SELECT 
        DATE_FORMAT(`mydb`.`venda`.`data`, '%m/%Y') AS `mes`,
        `mydb`.`loja`.`nomeFantasia` AS `nomeFantasia`,
        (SUM(`mydb`.`venda_has_produto`.`total`) + 0E0) AS `SomaDetotal`,
        (SUM(IF(ISNULL(`estq`.`valorunid`),
            (`pedidofornecedorreduzido`.`prcUnitario` * `mydb`.`venda_has_produto`.`quant`),
            ((`mydb`.`venda_has_produto`.`quant` * (1 - (0 * (`estq`.`desconto` / `estq`.`valor`)))) * `estq`.`valorunid`))) + 0E0) AS `custo`,
        IF((ISNULL(`estq`.`valorunid`)
                AND ISNULL(`pedidofornecedorreduzido`.`prcUnitario`)
                AND (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                1) <> 'R')
                AND (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                2) <> 'RD')),
            'sem custo',
            '') AS `sem custo`,
        (SUM(IF(((RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                1) = 'R')
                OR (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                2) = 'RD')),
            0,
            ((0.58 * 0.12) * IF(ISNULL(`estq`.`valorunid`),
                (`pedidofornecedorreduzido`.`prcUnitario` * `mydb`.`venda_has_produto`.`quant`),
                (`mydb`.`venda_has_produto`.`quant` * `estq`.`valorunid`))))) + 0E0) AS `ICMS`,
        SUM(IF(((RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                1) = 'R')
                OR (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                2) = 'RD')),
            (((`mydb`.`fornecedor`.`comissaoLoja` / 100) * `mydb`.`venda_has_produto`.`total`) * ((0.25 * 0.32) + (0.09 * 0.32))),
            (`mydb`.`venda_has_produto`.`total` * ((0.25 * 0.08) + (0.09 * 0.12))))) AS `IR+CSLL`,
        SUM(IF(((RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                1) = 'R')
                OR (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                2) = 'RD')),
            (((`mydb`.`fornecedor`.`comissaoLoja` / 100) * `mydb`.`venda_has_produto`.`total`) * 0.0865),
            (`mydb`.`venda_has_produto`.`total` * 0.0365))) AS `PIS+Cofins+ISS`,
        IF(((RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                1) = 'R')
                OR (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                2) = 'RD')),
            'Representacao',
            'Venda') AS `Representacao`,
        SUM(IF(((RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                1) = 'R')
                OR (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
                2) = 'RD')),
            ((`mydb`.`venda_has_produto`.`total` * `mydb`.`fornecedor`.`comissaoLoja`) / 100),
            0)) AS `Comissao Representacao`,
        (SUM(((`mydb`.`venda_has_produto`.`total` * `mydb`.`profissional`.`comissao`) / 100)) + 0E0) AS `RT`,
        SUM((((`mydb`.`fornecedor`.`comissao1` / 100) * `mydb`.`venda_has_produto`.`total`) - ((((RIGHT(`mydb`.`produto`.`ui`, 1) = 'L') * (`mydb`.`fornecedor`.`comissao2` - `mydb`.`fornecedor`.`comissao1`)) / 100) * `mydb`.`venda_has_produto`.`total`))) AS `ValorComissao`,
        IF((`k`.`Cartao` IS NOT NULL),
            ((-(1) * (`k`.`Cartao` * `mydb`.`venda_has_produto`.`total`)) / (`mydb`.`venda`.`total` - `mydb`.`venda`.`frete`)),
            0E0) AS `Cartao`
    FROM
        (((((((((`mydb`.`venda_has_produto`
        LEFT JOIN `mydb`.`venda` ON ((`mydb`.`venda_has_produto`.`idVenda` = `mydb`.`venda`.`idVenda`)))
        LEFT JOIN `mydb`.`usuario` ON ((`mydb`.`venda`.`idUsuario` = `mydb`.`usuario`.`idUsuario`)))
        LEFT JOIN `mydb`.`loja` ON ((`mydb`.`venda_has_produto`.`idLoja` = `mydb`.`loja`.`idLoja`)))
        LEFT JOIN (SELECT 
            LAST_INSERT_ID(`mydb`.`estoque_has_consumo`.`valorUnid`) AS `valorunid`,
                LAST_INSERT_ID(`mydb`.`estoque_has_consumo`.`desconto`) AS `desconto`,
                LAST_INSERT_ID(`mydb`.`estoque_has_consumo`.`valor`) AS `valor`,
                LAST_INSERT_ID(`mydb`.`estoque_has_consumo`.`idVendaProduto`) AS `idvendaproduto`
        FROM
            `mydb`.`estoque_has_consumo`
        GROUP BY `mydb`.`estoque_has_consumo`.`idVendaProduto`) `estq` ON ((`mydb`.`venda_has_produto`.`idVendaProduto` = `estq`.`idvendaproduto`)))
        LEFT JOIN (SELECT 
            `mydb`.`pedido_fornecedor_has_produto`.`idCompra` AS `idCompra`,
                `mydb`.`pedido_fornecedor_has_produto`.`idProduto` AS `idProduto`,
                LAST_INSERT_ID(`mydb`.`pedido_fornecedor_has_produto`.`prcUnitario`) AS `prcUnitario`
        FROM
            `mydb`.`pedido_fornecedor_has_produto`
        GROUP BY `mydb`.`pedido_fornecedor_has_produto`.`idCompra` , `mydb`.`pedido_fornecedor_has_produto`.`idProduto`) `pedidofornecedorreduzido` ON (((`mydb`.`venda_has_produto`.`idProduto` = `pedidofornecedorreduzido`.`idProduto`)
            AND (`mydb`.`venda_has_produto`.`idCompra` = `pedidofornecedorreduzido`.`idCompra`))))
        LEFT JOIN `mydb`.`fornecedor` ON ((`mydb`.`venda_has_produto`.`fornecedor` = `mydb`.`fornecedor`.`razaoSocial`)))
        LEFT JOIN `mydb`.`profissional` ON ((`mydb`.`venda`.`idProfissional` = `mydb`.`profissional`.`idProfissional`)))
        LEFT JOIN `mydb`.`produto` ON ((`mydb`.`venda_has_produto`.`idProduto` = `mydb`.`produto`.`idProduto`)))
        LEFT JOIN (SELECT 
            SUM(`mydb`.`conta_a_receber_has_pagamento`.`valor`) AS `Cartao`,
                `mydb`.`conta_a_receber_has_pagamento`.`idVenda` AS `idVenda`
        FROM
            `mydb`.`conta_a_receber_has_pagamento`
        WHERE
            ((`mydb`.`conta_a_receber_has_pagamento`.`status` <> 'CANCELADO')
                AND (`mydb`.`conta_a_receber_has_pagamento`.`tipo` LIKE '%Taxa Cartão')
                AND (`mydb`.`conta_a_receber_has_pagamento`.`desativado` = FALSE))
        GROUP BY `mydb`.`conta_a_receber_has_pagamento`.`idVenda`) `k` ON ((`k`.`idVenda` = `mydb`.`venda_has_produto`.`idVenda`)))
    WHERE
        (`mydb`.`venda_has_produto`.`status` <> 'CANCELADO')
    GROUP BY DATE_FORMAT(`mydb`.`venda`.`data`, '%Y-%m') , `mydb`.`loja`.`nomeFantasia` , IF((ISNULL(`estq`.`valorunid`)
            AND ISNULL(`pedidofornecedorreduzido`.`prcUnitario`)
            AND (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
            1) <> 'R')
            AND (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
            2) <> 'RD')),
        'sem custo',
        '') , IF(((RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
            1) = 'R')
            OR (RIGHT(`mydb`.`venda_has_produto`.`idVenda`,
            2) = 'RD')),
        'Representacao',
        'Venda') , IF((`k`.`Cartao` IS NOT NULL),
        ((`k`.`Cartao` * `mydb`.`venda_has_produto`.`total`) / (`mydb`.`venda`.`total` - `mydb`.`venda`.`frete`)),
        0);

