        (SUM(`mydb`.`venda_has_produto`.`total`) + 0E0) AS `SomaDetotal`,
        (SUM(((`mydb`.`venda`.`frete` / (`mydb`.`venda`.`total` - `mydb`.`venda`.`frete`)) * `mydb`.`venda_has_produto`.`total`)) + 0E0) AS `somadefrete`,
        SUM(IF(ISNULL(`estq`.`valor`),
            (`pedidofornecedorreduzido`.`prcUnitario` * `mydb`.`venda_has_produto`.`quant`),
            (`estq`.`valor` + 0E0))) AS `custo`,
        IF((ISNULL(`estq`.`valor`)
                AND ISNULL(`pedidofornecedorreduzido`.`prcUnitario`)
                AND (`mydb`.`venda`.`representacao` = 0)),
            'sem custo',
            '') AS `sem custo`,
        (SUM(IF((`mydb`.`venda`.`representacao` = 1),
            0,
            ((0.58 * 0.18) * IF(ISNULL(`estq`.`valor`),
                (`pedidofornecedorreduzido`.`prcUnitario` * `mydb`.`venda_has_produto`.`quant`),
                `estq`.`valor`)))) + 0E0) AS `ICMS`,
        SUM(IF((`mydb`.`venda`.`representacao` = 1),
            ((((`mydb`.`fornecedor`.`comissaoLoja` / 100) * `mydb`.`venda_has_produto`.`total`) * (1 - 0.0925)) * ((0.25 * 1) + (0.09 * 1))),
